import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { userAPI } from '../services/api';
import { toast } from 'react-hot-toast';

const Profile: React.FC = () => {
  const { user } = useAuth();
  const [verificationStatus, setVerificationStatus] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchVerificationStatus = async () => {
      try {
        const response = await userAPI.getVerificationStatus();
        if (response.success && response.data) {
          setVerificationStatus(response.data);
        }
      } catch (error: any) {
        console.error('Failed to fetch verification status:', error);
        toast.error('Failed to load verification status');
      } finally {
        setLoading(false);
      }
    };

    fetchVerificationStatus();
  }, []);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const formatPhoneNumber = (phone: string) => {
    // Format phone number for display
    return phone.replace(/(\+91)(\d{5})(\d{5})/, '$1 $2 $3');
  };

  const formatAadhaar = (aadhaar: string) => {
    // Format Aadhaar number for display (mask middle digits)
    return aadhaar.replace(/(\d{4})(\d{4})(\d{4})/, '$1 XXXX $3');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="spinner mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-600 rounded-xl p-8 text-white">
        <h1 className="text-3xl font-bold mb-2">
          Your Profile
        </h1>
        <p className="text-indigo-100">
          Manage your personal information and verification status
        </p>
      </div>

      {/* Personal Information */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">Personal Information</h2>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="form-label">Full Name</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">{user?.name}</p>
              </div>
            </div>
            <div>
              <label className="form-label">Aadhaar Number</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium font-mono">
                  {formatAadhaar(user?.aadhaarNumber || '')}
                </p>
              </div>
            </div>
            <div>
              <label className="form-label">Phone Number</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">
                  {formatPhoneNumber(user?.phoneNumber || '')}
                </p>
              </div>
            </div>
            <div>
              <label className="form-label">Date of Birth</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">
                  {formatDate(user?.dateOfBirth || '')}
                </p>
              </div>
            </div>
            <div>
              <label className="form-label">Age</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">{user?.age} years</p>
              </div>
            </div>
            <div>
              <label className="form-label">Gender</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium capitalize">{user?.gender}</p>
              </div>
            </div>
            <div>
              <label className="form-label">Occupation</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium capitalize">{user?.occupation}</p>
              </div>
            </div>
            <div>
              <label className="form-label">Annual Income</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">
                  {formatCurrency(user?.annualIncome || 0)}
                </p>
              </div>
            </div>
            <div>
              <label className="form-label">State</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">{user?.state}</p>
              </div>
            </div>
            <div>
              <label className="form-label">District</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">{user?.district}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Verification Status */}
      {verificationStatus && (
        <div className="card">
          <div className="card-header">
            <h2 className="text-xl font-semibold">Verification Status</h2>
          </div>
          <div className="card-body">
            <div className="flex items-center justify-between mb-6">
              <div>
                <h3 className="font-medium text-gray-900">
                  Account Verification
                </h3>
                <p className="text-sm text-gray-600">
                  Complete verification to access all schemes
                </p>
              </div>
              <div>
                {verificationStatus.isVerified ? (
                  <span className="badge badge-success">
                    ✓ Verified
                  </span>
                ) : (
                  <span className="badge badge-warning">
                    Pending Verification
                  </span>
                )}
              </div>
            </div>

            <div className="space-y-4">
              <h4 className="font-medium text-gray-900">Verification Steps</h4>
              {verificationStatus.verificationSteps?.map((step: any, index: number) => (
                <div key={index} className="flex items-center space-x-3">
                  <div className={`w-6 h-6 rounded-full flex items-center justify-center ${
                    step.completed 
                      ? 'bg-green-100 text-green-600' 
                      : 'bg-gray-100 text-gray-400'
                  }`}>
                    {step.completed ? '✓' : '○'}
                  </div>
                  <span className={`${
                    step.completed ? 'text-gray-900' : 'text-gray-500'
                  }`}>
                    {step.step}
                  </span>
                </div>
              ))}
            </div>

            {verificationStatus.documentsRequired && (
              <div className="mt-6 pt-6 border-t border-gray-200">
                <h4 className="font-medium text-gray-900 mb-3">
                  Documents Required
                </h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                  {verificationStatus.documentsRequired.map((doc: string, index: number) => (
                    <div key={index} className="flex items-center space-x-2">
                      <span className="text-sm text-gray-600">•</span>
                      <span className="text-sm text-gray-700">{doc}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Account Information */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">Account Information</h2>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="form-label">Account Created</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <p className="font-medium">
                  {formatDate(user?.createdAt || '')}
                </p>
              </div>
            </div>
            <div>
              <label className="form-label">Account Status</label>
              <div className="p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-2">
                  {user?.isVerified ? (
                    <>
                      <span className="w-2 h-2 bg-green-500 rounded-full"></span>
                      <span className="font-medium text-green-600">Active & Verified</span>
                    </>
                  ) : (
                    <>
                      <span className="w-2 h-2 bg-yellow-500 rounded-full"></span>
                      <span className="font-medium text-yellow-600">Active - Pending Verification</span>
                    </>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Actions */}
      <div className="card">
        <div className="card-body">
          <div className="flex flex-col sm:flex-row gap-4">
            <button className="btn btn-primary">
              Update Profile
            </button>
            <button className="btn btn-secondary">
              Download Profile Summary
            </button>
            <button className="btn btn-secondary">
              Privacy Settings
            </button>
          </div>
          <div className="mt-6 pt-6 border-t border-gray-200">
            <p className="text-sm text-gray-600">
              <strong>Note:</strong> This is a demonstration with mock data. 
              In a real application, you would be able to edit your profile information 
              and upload verification documents.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
