import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { authAPI } from '../services/api';
import toast from 'react-hot-toast';

const Login: React.FC = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const [autoFilledOtp, setAutoFilledOtp] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  // Demo phone numbers for testing
  const demoNumbers = [
    '+919876543210',
    '+919876543211',
    '+919876543212',
    '+919876543213',
    '+919876543214'
  ];

  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    }
  }, [countdown]);

  const handleSendOTP = async () => {
    if (!phoneNumber.trim()) {
      toast.error('Please enter your phone number');
      return;
    }

    if (!demoNumbers.includes(phoneNumber)) {
      toast.error('Please use a demo phone number for testing');
      return;
    }

    setIsLoading(true);
    try {
      const response = await authAPI.sendOTP(phoneNumber);
      if (response.success) {
        setIsOtpSent(true);
        setCountdown(30);
        
        // Auto-fill OTP for demo
        const generatedOtp = response.data?.otp || '123456';
        setAutoFilledOtp(generatedOtp);
        setOtp(generatedOtp);
        
        toast.success(`OTP sent! Demo OTP: ${generatedOtp}`, {
          duration: 8000,
          style: {
            background: '#10b981',
            color: 'white',
            fontWeight: 'bold'
          }
        });
      } else {
        toast.error(response.message || 'Failed to send OTP');
      }
    } catch (error) {
      console.error('OTP send error:', error);
      toast.error('Network error. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleVerifyOTP = async () => {
    if (!otp.trim()) {
      toast.error('Please enter the OTP');
      return;
    }

    setIsVerifying(true);
    try {
      await login(phoneNumber, otp);
      toast.success('Login successful! Welcome to Government Schemes Portal', {
        duration: 3000,
        style: {
          background: '#10b981',
          color: 'white'
        }
      });
      navigate('/dashboard');
    } catch (error) {
      console.error('OTP verification error:', error);
      toast.error('Verification failed. Please try again.');
    } finally {
      setIsVerifying(false);
    }
  };

  const handlePhoneNumberSelect = (number: string) => {
    setPhoneNumber(number);
    setIsOtpSent(false);
    setOtp('');
    setAutoFilledOtp('');
  };

  const handleResendOTP = async () => {
    if (countdown > 0) return;
    await handleSendOTP();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-indigo-50 flex items-center justify-center p-4">
      <div className="max-w-md w-full space-y-8">
        {/* Header */}
        <div className="text-center">
          <div className="flex justify-center mb-6">
            <div className="w-16 h-16 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg">
              <span className="text-white font-bold text-2xl">G</span>
            </div>
          </div>
          <h2 className="text-3xl font-bold text-gray-900 mb-2">
            Welcome Back
          </h2>
          <p className="text-gray-600">
            Sign in to access your government schemes
          </p>
        </div>

        {/* Demo Numbers */}
        <div className="bg-amber-50 border border-amber-200 rounded-lg p-4">
          <h3 className="text-sm font-medium text-amber-800 mb-3">Demo Phone Numbers</h3>
          <div className="grid grid-cols-1 gap-2">
            {demoNumbers.map((number) => (
              <button
                key={number}
                onClick={() => handlePhoneNumberSelect(number)}
                className={`text-left px-3 py-2 rounded-md text-sm transition-colors ${
                  phoneNumber === number
                    ? 'bg-amber-200 text-amber-900 font-medium'
                    : 'bg-white text-gray-700 hover:bg-amber-100'
                }`}
              >
                {number}
              </button>
            ))}
          </div>
        </div>

        {/* Login Form */}
        <div className="bg-white rounded-xl shadow-lg p-8 border border-gray-200">
          <div className="space-y-6">
            {/* Phone Number Input */}
            <div>
              <label htmlFor="phoneNumber" className="form-label">
                Phone Number
              </label>
              <input
                id="phoneNumber"
                type="tel"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                className="form-input"
                placeholder="Enter your phone number"
                disabled={isOtpSent}
              />
            </div>

            {/* OTP Input */}
            {isOtpSent && (
              <div>
                <label htmlFor="otp" className="form-label">
                  Enter OTP
                </label>
                <input
                  id="otp"
                  type="text"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  className="form-input"
                  placeholder="Enter 6-digit OTP"
                  maxLength={6}
                />
                {autoFilledOtp && (
                  <p className="text-xs text-green-600 mt-1">
                    Demo OTP has been auto-filled: {autoFilledOtp}
                  </p>
                )}
              </div>
            )}

            {/* Action Buttons */}
            <div className="space-y-4">
              {!isOtpSent ? (
                <button
                  onClick={handleSendOTP}
                  disabled={isLoading || !phoneNumber.trim()}
                  className="w-full btn btn-primary btn-lg disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isLoading ? (
                    <div className="flex items-center justify-center space-x-2">
                      <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                      <span>Sending OTP...</span>
                    </div>
                  ) : (
                    'Send OTP'
                  )}
                </button>
              ) : (
                <div className="space-y-3">
                  <button
                    onClick={handleVerifyOTP}
                    disabled={isVerifying || !otp.trim()}
                    className="w-full btn btn-primary btn-lg disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isVerifying ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                        <span>Verifying...</span>
                      </div>
                    ) : (
                      'Verify & Login'
                    )}
                  </button>
                  
                  <button
                    onClick={handleResendOTP}
                    disabled={countdown > 0}
                    className="w-full btn btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {countdown > 0 ? `Resend OTP in ${countdown}s` : 'Resend OTP'}
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="text-center text-sm text-gray-500">
          <p>
            Secure access to government schemes and services
          </p>
          <p className="mt-2">
            Powered by Digital India Initiative
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
