import { useAuth } from '../contexts/AuthContext';
import { schemesAPI } from '../services/api';
import { Scheme } from '../types';
import { Link } from 'react-router-dom';
import Loading from '../components/Loading';

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const [schemes, setSchemes] = useState<Scheme[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await getSchemes();
        if (response.success) {
          setSchemes(response.data);
        } else {
          setError(response.message || 'Failed to load schemes');
        }
      } catch (err) {
        setError('Network error. Please try again.');
        console.error('Dashboard error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchSchemes();
  }, []);

  if (loading) return <Loading />;

  const eligibleSchemes = schemes.filter(scheme => 
    scheme.status === 'active' && scheme.category
  );
  const ineligibleSchemes = schemes.filter(scheme => 
    scheme.status !== 'active' || !scheme.category
  );

  const eligibilityScore = eligibleSchemes.length > 0 
    ? Math.round((eligibleSchemes.length / schemes.length) * 100)
    : 0;

  const stats = [
    {
      title: 'Total Schemes',
      value: schemes.length,
      subtitle: 'Available schemes',
      color: 'bg-blue-500',
      bgColor: 'bg-blue-50',
      textColor: 'text-blue-700'
    },
    {
      title: 'Eligible Schemes',
      value: eligibleSchemes.length,
      subtitle: 'You can apply',
      color: 'bg-green-500',
      bgColor: 'bg-green-50',
      textColor: 'text-green-700'
    },
    {
      title: 'Not Eligible',
      value: ineligibleSchemes.length,
      subtitle: 'Review requirements',
      color: 'bg-orange-500',
      bgColor: 'bg-orange-50',
      textColor: 'text-orange-700'
    },
    {
      title: 'Eligibility Score',
      value: `${eligibilityScore}%`,
      subtitle: 'Profile completion',
      color: 'bg-purple-500',
      bgColor: 'bg-purple-50',
      textColor: 'text-purple-700'
    }
  ];

  return (
    <div className="space-y-8">
      {/* Welcome Header */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 rounded-2xl p-8 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold mb-2">
              Welcome back, {user?.name}!
            </h1>
            <p className="text-blue-100 text-lg">
              Here's your personalized government schemes dashboard
            </p>
          </div>
          <div className="hidden md:block">
            <div className="w-20 h-20 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
              <span className="text-3xl font-bold">
                {user?.name?.charAt(0)}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <div className="w-5 h-5 bg-red-500 rounded-full"></div>
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
            </div>
          </div>
        </div>
      )}

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => (
          <div key={index} className={`${stat.bgColor} rounded-xl p-6 border border-gray-200`}>
            <div className="flex items-center justify-between">
              <div>
                <p className={`text-sm font-medium ${stat.textColor} opacity-75`}>
                  {stat.title}
                </p>
                <p className={`text-3xl font-bold ${stat.textColor} mt-2`}>
                  {stat.value}
                </p>
                <p className={`text-sm ${stat.textColor} opacity-75 mt-1`}>
                  {stat.subtitle}
                </p>
              </div>
              <div className={`w-12 h-12 ${stat.color} rounded-lg flex items-center justify-center`}>
                <div className="w-6 h-6 bg-white rounded-full"></div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Link
            to="/schemes"
            className="bg-blue-50 hover:bg-blue-100 p-4 rounded-lg transition-colors duration-200 group"
          >
            <h3 className="font-medium text-blue-900 group-hover:text-blue-800">
              Browse All Schemes
            </h3>
            <p className="text-sm text-blue-700 mt-1">
              Explore available government schemes
            </p>
          </Link>
          
          <Link
            to="/profile"
            className="bg-green-50 hover:bg-green-100 p-4 rounded-lg transition-colors duration-200 group"
          >
            <h3 className="font-medium text-green-900 group-hover:text-green-800">
              Update Profile
            </h3>
            <p className="text-sm text-green-700 mt-1">
              Keep your information current
            </p>
          </Link>
          
          <Link
            to="/documents"
            className="bg-purple-50 hover:bg-purple-100 p-4 rounded-lg transition-colors duration-200 group"
          >
            <h3 className="font-medium text-purple-900 group-hover:text-purple-800">
              Manage Documents
            </h3>
            <p className="text-sm text-purple-700 mt-1">
              View and organize your documents
            </p>
          </Link>
        </div>
      </div>

      {/* Profile Section */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-6">Your Profile</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="space-y-4">
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">Full Name</span>
              <span className="text-sm text-gray-900">{user?.name}</span>
            </div>
            
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">Phone Number</span>
              <span className="text-sm text-gray-900">{user?.phoneNumber}</span>
            </div>
            
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">Age</span>
              <span className="text-sm text-gray-900">{user?.age || 'Not specified'}</span>
            </div>
            
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">Gender</span>
              <span className="text-sm text-gray-900">{user?.gender || 'Not specified'}</span>
            </div>
          </div>
          
          <div className="space-y-4">
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">City</span>
              <span className="text-sm text-gray-900">{user?.city || 'Not specified'}</span>
            </div>
            
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">State</span>
              <span className="text-sm text-gray-900">{user?.state || 'Not specified'}</span>
            </div>
            
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">Income</span>
              <span className="text-sm text-gray-900">
                {user?.income ? `₹${user.income.toLocaleString()}` : 'Not specified'}
              </span>
            </div>
            
            <div className="flex items-center justify-between py-3 border-b border-gray-100">
              <span className="text-sm font-medium text-gray-600">Profile Status</span>
              <span className={`text-sm px-2 py-1 rounded-full ${
                user?.profileCompleted 
                  ? 'bg-green-100 text-green-800' 
                  : 'bg-yellow-100 text-yellow-800'
              }`}>
                {user?.profileCompleted ? 'Complete' : 'Incomplete'}
              </span>
            </div>
          </div>
        </div>
        
        <div className="mt-6 pt-6 border-t border-gray-200">
          <Link
            to="/profile"
            className="inline-flex items-center justify-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 transition-colors duration-200"
          >
            Edit Profile
          </Link>
        </div>
      </div>

      {/* Recent Schemes */}
      {eligibleSchemes.length > 0 && (
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-bold text-gray-900">Eligible Schemes</h2>
            <Link
              to="/schemes"
              className="text-sm text-blue-600 hover:text-blue-700 font-medium"
            >
              View All
            </Link>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {eligibleSchemes.slice(0, 4).map((scheme) => (
              <Link
                key={scheme.id}
                to={`/schemes/${scheme.id}`}
                className="block p-4 border border-gray-200 rounded-lg hover:shadow-md transition-shadow duration-200"
              >
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <h3 className="font-medium text-gray-900 mb-2">
                      {scheme.title}
                    </h3>
                    <p className="text-sm text-gray-600 mb-2">
                      {scheme.description?.substring(0, 100)}...
                    </p>
                    <div className="flex items-center space-x-2">
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                        {scheme.category}
                      </span>
                      <span className="text-xs text-gray-500">
                        {scheme.benefitsAmount ? `₹${scheme.benefitsAmount.toLocaleString()}` : 'Benefits available'}
                      </span>
                    </div>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
