import React, { useState, useEffect } from 'react';
import { schemesAPI } from '../services/api';
import { Scheme } from '../types';
import { Link } from 'react-router-dom';
import Loading from '../components/Loading';

const Schemes: React.FC = () => {
  const [schemes, setSchemes] = useState<Scheme[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedStatus, setSelectedStatus] = useState('all');

  const categories = [
    { value: 'all', label: 'All Categories', icon: 'üìã' },
    { value: 'agriculture', label: 'Agriculture', icon: 'üåæ' },
    { value: 'education', label: 'Education', icon: 'üéì' },
    { value: 'healthcare', label: 'Healthcare', icon: 'üè•' },
    { value: 'housing', label: 'Housing', icon: 'üè†' },
    { value: 'employment', label: 'Employment', icon: 'üíº' },
    { value: 'social-security', label: 'Social Security', icon: 'üõ°Ô∏è' },
    { value: 'business', label: 'Business', icon: 'üíº' },
    { value: 'women', label: 'Women', icon: 'üë©' },
    { value: 'senior-citizen', label: 'Senior Citizen', icon: 'üë¥' },
    { value: 'youth', label: 'Youth', icon: 'üë¶' }
  ];

  const statuses = [
    { value: 'all', label: 'All Statuses' },
    { value: 'active', label: 'Active' },
    { value: 'inactive', label: 'Inactive' },
    { value: 'suspended', label: 'Suspended' }
  ];

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await schemesAPI.getAllSchemes();
        if (response.success) {
          setSchemes(response.data || []);
        } else {
          setError(response.message || 'Failed to load schemes');
        }
      } catch (err) {
        setError('Network error. Please try again.');
        console.error('Schemes error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchSchemes();
  }, []);

  const filteredSchemes = schemes.filter(scheme => {
    const matchesSearch = scheme.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         scheme.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || scheme.category === selectedCategory;
    const matchesStatus = selectedStatus === 'all' || scheme.status === selectedStatus;
    
    return matchesSearch && matchesCategory && matchesStatus;
  });

  const getCategoryColor = (category: string) => {
    const colors: { [key: string]: string } = {
      'agriculture': 'bg-green-100 text-green-800 border-green-200',
      'education': 'bg-blue-100 text-blue-800 border-blue-200',
      'healthcare': 'bg-red-100 text-red-800 border-red-200',
      'housing': 'bg-yellow-100 text-yellow-800 border-yellow-200',
      'employment': 'bg-purple-100 text-purple-800 border-purple-200',
      'social-security': 'bg-indigo-100 text-indigo-800 border-indigo-200',
      'business': 'bg-orange-100 text-orange-800 border-orange-200',
      'women': 'bg-pink-100 text-pink-800 border-pink-200',
      'senior-citizen': 'bg-gray-100 text-gray-800 border-gray-200',
      'youth': 'bg-cyan-100 text-cyan-800 border-cyan-200'
    };
    return colors[category] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getStatusColor = (status: string) => {
    const colors: { [key: string]: string } = {
      'active': 'bg-green-100 text-green-800 border-green-200',
      'inactive': 'bg-gray-100 text-gray-800 border-gray-200',
      'suspended': 'bg-red-100 text-red-800 border-red-200'
    };
    return colors[status] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getCategoryIcon = (category: string) => {
    const icons: { [key: string]: string } = {
      'agriculture': 'üåæ',
      'education': 'üéì',
      'healthcare': 'üè•',
      'housing': 'üè†',
      'employment': 'üíº',
      'social-security': 'üõ°Ô∏è',
      'business': 'üíº',
      'women': 'üë©',
      'senior-citizen': 'üë¥',
      'youth': 'üë¶'
    };
    return icons[category] || 'üìã';
  };

  if (loading) return <Loading />;

  return (
    <div className="space-y-6">
      {/* Enhanced Header */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 rounded-xl p-6 text-white shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold mb-2">Government Schemes</h1>
            <p className="text-blue-100">
              Discover and apply for government schemes designed to help you
            </p>
          </div>
          <div className="hidden md:flex items-center space-x-4">
            <div className="w-12 h-12 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
              <span className="text-2xl">üìã</span>
            </div>
          </div>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex items-center">
            <span className="text-red-500 mr-3">‚ö†Ô∏è</span>
            <p className="text-sm text-red-800">{error}</p>
          </div>
        </div>
      )}

      {/* Enhanced Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Schemes</p>
              <p className="text-2xl font-bold text-gray-900 mt-1">{schemes.length}</p>
            </div>
            <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
              <span className="text-xl">üìã</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Active</p>
              <p className="text-2xl font-bold text-green-600 mt-1">
                {schemes.filter(s => s.status === 'active').length}
              </p>
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
              <span className="text-xl">‚úÖ</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Categories</p>
              <p className="text-2xl font-bold text-purple-600 mt-1">
                {new Set(schemes.map(s => s.category)).size}
              </p>
            </div>
            <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center">
              <span className="text-xl">üìä</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Filtered</p>
              <p className="text-2xl font-bold text-orange-600 mt-1">{filteredSchemes.length}</p>
            </div>
            <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
              <span className="text-xl">üîç</span>
            </div>
          </div>
        </div>
      </div>

      {/* Enhanced Search and Filters */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
        <h2 className="text-lg font-bold text-gray-900 mb-4">Search & Filter</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="md:col-span-2">
            <label className="block text-sm font-medium text-gray-700 mb-2">Search Schemes</label>
            <div className="relative">
              <input
                type="text"
                placeholder="Search by name or description..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-4 py-3 pl-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">üîç</span>
            </div>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Category</label>
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              {categories.map(cat => (
                <option key={cat.value} value={cat.value}>
                  {cat.icon} {cat.label}
                </option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              {statuses.map(status => (
                <option key={status.value} value={status.value}>{status.label}</option>
              ))}
            </select>
          </div>
        </div>

        {(searchTerm || selectedCategory !== 'all' || selectedStatus !== 'all') && (
          <div className="mt-4 flex items-center justify-between">
            <p className="text-sm text-gray-600">
              Showing <span className="font-medium">{filteredSchemes.length}</span> of <span className="font-medium">{schemes.length}</span> schemes
            </p>
            <button
              onClick={() => {
                setSearchTerm('');
                setSelectedCategory('all');
                setSelectedStatus('all');
              }}
              className="text-sm text-blue-600 hover:text-blue-700 font-medium hover:underline"
            >
              Clear Filters
            </button>
          </div>
        )}
      </div>

      {/* Enhanced Schemes Grid with Consistent Heights */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredSchemes.map((scheme) => (
          <div key={scheme.id} className="bg-white rounded-xl shadow-sm border border-gray-200 hover:shadow-lg hover:border-blue-300 transition-all duration-200 group flex flex-col h-full">
            {/* Card Header */}
            <div className="p-6 pb-4 flex-shrink-0">
              <div className="flex items-start justify-between mb-3">
                <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-lg flex items-center justify-center text-white text-xl">
                  {getCategoryIcon(scheme.category)}
                </div>
                <div className="flex flex-col space-y-1">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${getCategoryColor(scheme.category)}`}>
                    {scheme.category}
                  </span>
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${getStatusColor(scheme.status)}`}>
                    {scheme.status}
                  </span>
                </div>
              </div>
              
              <h3 className="text-lg font-bold text-gray-900 mb-2 group-hover:text-blue-600 transition-colors">
                {scheme.name}
              </h3>
            </div>

            {/* Card Body - Flexible Height */}
            <div className="px-6 pb-4 flex-grow">
              <p className="text-sm text-gray-600 mb-4 line-clamp-3">{scheme.description}</p>
              
              <div className="space-y-2 text-sm">
                <div className="flex items-center justify-between">
                  <span className="text-gray-500">Department:</span>
                  <span className="font-medium text-gray-900">{scheme.department}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-500">Processing:</span>
                  <span className="font-medium text-gray-900">{scheme.processingTime}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-500">Fee:</span>
                  <span className="font-medium text-green-600">‚Çπ{scheme.applicationFee}</span>
                </div>
              </div>
            </div>

            {/* Card Footer - Fixed Height */}
            <div className="p-6 pt-4 border-t border-gray-100 flex-shrink-0">
              <Link
                to={`/schemes/${scheme.id}`}
                className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white py-3 px-4 rounded-lg font-medium transition-all duration-200 text-center block shadow-md hover:shadow-lg transform hover:scale-105 group-hover:scale-105"
              >
                View Details ‚Üí
              </Link>
            </div>
          </div>
        ))}
      </div>

      {/* Enhanced No Results */}
      {filteredSchemes.length === 0 && (
        <div className="text-center py-12">
          <div className="w-20 h-20 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-6">
            <span className="text-3xl">üìã</span>
          </div>
          <h3 className="text-xl font-bold text-gray-900 mb-2">No Schemes Found</h3>
          <p className="text-gray-600 mb-4">Try adjusting your search criteria or filters.</p>
          <button
            onClick={() => {
              setSearchTerm('');
              setSelectedCategory('all');
              setSelectedStatus('all');
            }}
            className="text-blue-600 hover:text-blue-700 font-medium hover:underline"
          >
            Clear all filters
          </button>
        </div>
      )}
    </div>
  );
};

export default Schemes;
