import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { schemesAPI } from '../services/api';
import { Scheme, EligibilityResult, EligibilityStats } from '../types';
import { toast } from 'react-hot-toast';

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [eligibleSchemes, setEligibleSchemes] = useState<Scheme[]>([]);
  const [eligibilityResults, setEligibilityResults] = useState<EligibilityResult[]>([]);
  const [stats, setStats] = useState<EligibilityStats | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const [eligibleRes, allResultsRes, statsRes] = await Promise.all([
          schemesAPI.getEligibleSchemes(),
          schemesAPI.getEligibilityResults(),
          schemesAPI.getEligibilityStats(),
        ]);

        if (eligibleRes.success && eligibleRes.data) {
          setEligibleSchemes(eligibleRes.data);
        }

        if (allResultsRes.success && allResultsRes.data) {
          setEligibilityResults(allResultsRes.data);
        }

        if (statsRes.success && statsRes.data) {
          setStats(statsRes.data);
        }
      } catch (error: any) {
        console.error('Failed to fetch dashboard data:', error);
        toast.error('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const handleSchemeClick = (schemeId: string) => {
    navigate(`/schemes/${schemeId}`);
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getEligibilityColor = (score: number) => {
    if (score >= 100) return 'text-green-600';
    if (score >= 75) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getEligibilityBadge = (score: number) => {
    if (score >= 100) return 'badge-success';
    if (score >= 75) return 'badge-warning';
    return 'badge-secondary';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="spinner mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-600 rounded-xl p-8 text-white">
        <h1 className="text-3xl font-bold mb-2">
          Welcome, {user?.name}! ðŸ‘‹
        </h1>
        <p className="text-indigo-100 text-lg">
          Here's your personalized government schemes dashboard
        </p>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="card">
            <div className="card-body text-center">
              <div className="text-3xl font-bold text-indigo-600">
                {stats.totalSchemes}
              </div>
              <div className="text-sm text-gray-600">Total Schemes</div>
            </div>
          </div>
          <div className="card">
            <div className="card-body text-center">
              <div className="text-3xl font-bold text-green-600">
                {stats.eligibleSchemes}
              </div>
              <div className="text-sm text-gray-600">Eligible Schemes</div>
            </div>
          </div>
          <div className="card">
            <div className="card-body text-center">
              <div className="text-3xl font-bold text-yellow-600">
                {stats.ineligibleSchemes}
              </div>
              <div className="text-sm text-gray-600">Not Eligible</div>
            </div>
          </div>
          <div className="card">
            <div className="card-body text-center">
              <div className="text-3xl font-bold text-indigo-600">
                {stats.eligibilityPercentage}%
              </div>
              <div className="text-sm text-gray-600">Eligibility Score</div>
            </div>
          </div>
        </div>
      )}

      {/* User Profile Summary */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">Your Profile</h2>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div>
              <span className="text-sm text-gray-600">Age</span>
              <p className="font-medium">{user?.age} years</p>
            </div>
            <div>
              <span className="text-sm text-gray-600">Occupation</span>
              <p className="font-medium capitalize">{user?.occupation}</p>
            </div>
            <div>
              <span className="text-sm text-gray-600">Annual Income</span>
              <p className="font-medium">{formatCurrency(user?.annualIncome || 0)}</p>
            </div>
            <div>
              <span className="text-sm text-gray-600">Location</span>
              <p className="font-medium">{user?.district}, {user?.state}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Eligible Schemes */}
      <div className="card">
        <div className="card-header">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">
              Schemes You're Eligible For ({eligibleSchemes.length})
            </h2>
            <button
              onClick={() => navigate('/schemes')}
              className="btn btn-primary btn-sm"
            >
              View All Schemes
            </button>
          </div>
        </div>
        <div className="card-body">
          {eligibleSchemes.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-500">
                No schemes found matching your current profile.
              </p>
              <button
                onClick={() => navigate('/profile')}
                className="btn btn-secondary mt-4"
              >
                Update Profile
              </button>
            </div>
          ) : (
            <div className="space-y-4">
              {eligibleSchemes.slice(0, 3).map((scheme) => (
                <div
                  key={scheme.id}
                  className="border border-gray-200 rounded-lg p-4 hover:border-indigo-300 hover:bg-indigo-50 cursor-pointer transition-colors"
                  onClick={() => handleSchemeClick(scheme.id)}
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h3 className="font-medium text-gray-900 mb-1">
                        {scheme.name}
                      </h3>
                      <p className="text-sm text-gray-600 mb-2">
                        {scheme.description}
                      </p>
                      <div className="flex items-center space-x-4">
                        <span className="badge badge-primary">
                          {scheme.category}
                        </span>
                        <span className="text-sm text-gray-500">
                          {scheme.department}
                        </span>
                      </div>
                    </div>
                    <div className="ml-4">
                      <span className="badge badge-success">
                        âœ“ Eligible
                      </span>
                    </div>
                  </div>
                </div>
              ))}
              {eligibleSchemes.length > 3 && (
                <div className="text-center pt-4">
                  <button
                    onClick={() => navigate('/schemes')}
                    className="btn btn-secondary"
                  >
                    View {eligibleSchemes.length - 3} More Schemes
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* All Schemes with Eligibility */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">
            All Schemes Overview
          </h2>
        </div>
        <div className="card-body">
          <div className="space-y-3">
            {eligibilityResults.slice(0, 5).map((result) => (
              <div
                key={result.scheme.id}
                className="flex items-center justify-between p-3 border border-gray-200 rounded-lg hover:border-indigo-300 cursor-pointer transition-colors"
                onClick={() => handleSchemeClick(result.scheme.id)}
              >
                <div className="flex-1">
                  <h4 className="font-medium text-gray-900">
                    {result.scheme.name}
                  </h4>
                  <p className="text-sm text-gray-500">
                    {result.scheme.category}
                  </p>
                </div>
                <div className="flex items-center space-x-3">
                  <span className={`text-sm font-medium ${getEligibilityColor(result.eligibilityScore)}`}>
                    {result.eligibilityScore}%
                  </span>
                  <span className={`badge ${getEligibilityBadge(result.eligibilityScore)}`}>
                    {result.isEligible ? 'Eligible' : 'Not Eligible'}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
