import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { schemesAPI } from '../services/api';
import { Scheme } from '../types';
import { Link } from 'react-router-dom';
import Loading from '../components/Loading';

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const [schemes, setSchemes] = useState<Scheme[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await schemesAPI.getAllSchemes();
        if (response.success) {
          setSchemes(response.data || []);
        } else {
          setError(response.message || 'Failed to load schemes');
        }
      } catch (err) {
        setError('Network error. Please try again.');
        console.error('Dashboard error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchSchemes();
  }, []);

  if (loading) return <Loading />;

  const eligibleSchemes = schemes.filter(scheme => 
    scheme.status === 'active' && scheme.category
  );

  const eligibilityScore = eligibleSchemes.length > 0 
    ? Math.round((eligibleSchemes.length / schemes.length) * 100)
    : 0;

  const stats = [
    {
      title: 'Available Schemes',
      value: schemes.length,
      subtitle: 'Total schemes',
      color: 'from-blue-500 to-blue-600',
      bgColor: 'bg-blue-50',
      textColor: 'text-blue-700',
      icon: 'üìã',
      link: '/schemes'
    },
    {
      title: 'Eligible For',
      value: eligibleSchemes.length,
      subtitle: 'You can apply',
      color: 'from-green-500 to-green-600',
      bgColor: 'bg-green-50',
      textColor: 'text-green-700',
      icon: '‚úÖ',
      link: '/schemes?filter=eligible'
    },
    {
      title: 'Profile Score',
      value: `${eligibilityScore}%`,
      subtitle: 'Completion rate',
      color: 'from-purple-500 to-purple-600',
      bgColor: 'bg-purple-50',
      textColor: 'text-purple-700',
      icon: 'üéØ',
      link: '/profile'
    }
  ];

  return (
    <div className="space-y-6">
      {/* Compact Welcome Header */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 rounded-xl p-6 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold mb-1">
              Welcome back, {user?.name}!
            </h1>
            <p className="text-blue-100">
              Your personalized government schemes dashboard
            </p>
          </div>
          <div className="hidden md:flex items-center space-x-4">
            <div className="text-right">
              <p className="text-sm text-blue-200">Verified Citizen</p>
              <p className="text-lg font-semibold">{user?.name?.charAt(0)}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex items-center">
            <span className="text-red-500 mr-3">‚ö†Ô∏è</span>
            <p className="text-sm text-red-800">{error}</p>
          </div>
        </div>
      )}

      {/* Stats Grid - Clickable Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {stats.map((stat, index) => (
          <Link
            key={index}
            to={stat.link}
            className={`${stat.bgColor} rounded-xl p-6 border border-gray-200 hover:shadow-lg transition-all duration-200 transform hover:scale-105 cursor-pointer group`}
          >
            <div className="flex items-center justify-between">
              <div>
                <p className={`text-sm font-medium ${stat.textColor} opacity-75 group-hover:opacity-100`}>
                  {stat.title}
                </p>
                <p className={`text-3xl font-bold ${stat.textColor} mt-2 group-hover:scale-110 transition-transform`}>
                  {stat.value}
                </p>
                <p className={`text-sm ${stat.textColor} opacity-75 mt-1`}>
                  {stat.subtitle}
                </p>
              </div>
              <div className={`w-12 h-12 bg-gradient-to-br ${stat.color} rounded-lg flex items-center justify-center text-white text-xl group-hover:scale-110 transition-transform shadow-lg`}>
                {stat.icon}
              </div>
            </div>
          </Link>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Enhanced Profile Section */}
        <div className="lg:col-span-1 bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <div className="text-center mb-6">
            <div className="w-20 h-20 bg-gradient-to-br from-orange-500 to-green-500 rounded-full flex items-center justify-center mx-auto mb-4">
              <span className="text-white text-2xl font-bold">{user?.name?.charAt(0)}</span>
            </div>
            <h3 className="text-lg font-bold text-gray-900">{user?.name}</h3>
            <p className="text-sm text-gray-600">{user?.occupation || 'Government Citizen'}</p>
            <div className="flex justify-center mt-3">
              <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium ${
                user?.isVerified ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
              }`}>
                {user?.isVerified ? '‚úÖ Verified' : '‚è≥ Pending Verification'}
              </span>
            </div>
          </div>
          
          <div className="space-y-3">
            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-sm text-gray-600">Age</span>
              <span className="text-sm font-medium text-gray-900">{user?.age || 'N/A'}</span>
            </div>
            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-sm text-gray-600">State</span>
              <span className="text-sm font-medium text-gray-900">{user?.state || 'N/A'}</span>
            </div>
            <div className="flex justify-between items-center py-2">
              <span className="text-sm text-gray-600">Income</span>
              <span className="text-sm font-medium text-gray-900">
                {user?.annualIncome ? `‚Çπ${user.annualIncome.toLocaleString()}` : 'N/A'}
              </span>
            </div>
          </div>
          
          <Link
            to="/profile"
            className="w-full mt-6 bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white py-2 px-4 rounded-lg font-medium transition-all duration-200 text-center block"
          >
            View Full Profile
          </Link>
        </div>

        {/* Main Content Area */}
        <div className="lg:col-span-2 space-y-6">
          {/* Quick Actions - Smaller and Better Positioned */}
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
            <h2 className="text-lg font-bold text-gray-900 mb-4">Quick Actions</h2>
            <div className="grid grid-cols-2 gap-4">
              <Link
                to="/schemes"
                className="bg-blue-50 hover:bg-blue-100 p-4 rounded-lg transition-colors duration-200 group text-center"
              >
                <div className="text-2xl mb-2 group-hover:scale-110 transition-transform">üìã</div>
                <h3 className="font-medium text-blue-900 text-sm group-hover:text-blue-800">
                  Browse Schemes
                </h3>
              </Link>
              
              <Link
                to="/documents"
                className="bg-green-50 hover:bg-green-100 p-4 rounded-lg transition-colors duration-200 group text-center"
              >
                <div className="text-2xl mb-2 group-hover:scale-110 transition-transform">üìÑ</div>
                <h3 className="font-medium text-green-900 text-sm group-hover:text-green-800">
                  Documents
                </h3>
              </Link>
            </div>
          </div>

          {/* Eligible Schemes */}
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-lg font-bold text-gray-900">Your Eligible Schemes</h2>
              <Link
                to="/schemes"
                className="text-blue-600 hover:text-blue-700 text-sm font-medium"
              >
                View All ‚Üí
              </Link>
            </div>
            
            {eligibleSchemes.length === 0 ? (
              <div className="text-center py-8">
                <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-2xl">üìã</span>
                </div>
                <p className="text-gray-600">No eligible schemes found</p>
                <Link
                  to="/schemes"
                  className="text-blue-600 hover:text-blue-700 text-sm mt-2 inline-block"
                >
                  Explore all schemes
                </Link>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {eligibleSchemes.slice(0, 4).map((scheme) => (
                  <Link
                    key={scheme.id}
                    to={`/schemes/${scheme.id}`}
                    className="border border-gray-200 rounded-lg p-4 hover:shadow-md hover:border-blue-300 transition-all duration-200 group"
                  >
                    <h3 className="font-medium text-gray-900 mb-2 group-hover:text-blue-600">
                      {scheme.name}
                    </h3>
                    <p className="text-sm text-gray-600 mb-3 line-clamp-2">
                      {scheme.description?.substring(0, 80)}...
                    </p>
                    <div className="flex items-center justify-between">
                      <span className="text-xs px-2 py-1 bg-blue-100 text-blue-800 rounded-full">
                        {scheme.category}
                      </span>
                      <span className="text-xs text-green-600 font-medium">
                        Eligible ‚úÖ
                      </span>
                    </div>
                  </Link>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
