import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { userAPI } from '../services/api';
import { UserDocuments } from '../types';
import { toast } from 'react-hot-toast';

interface DocumentCard {
  id: string;
  title: string;
  icon: string;
  description: string;
  available: boolean;
}

const Documents: React.FC = () => {
  const navigate = useNavigate();
  const [documents, setDocuments] = useState<UserDocuments | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await userAPI.getUserDocuments();
      if (response.success && response.data) {
        setDocuments(response.data);
      }
    } catch (error: any) {
      console.error('Failed to fetch documents:', error);
      toast.error('Failed to load documents');
    } finally {
      setLoading(false);
    }
  };

  const documentCards: DocumentCard[] = [
    {
      id: 'aadhaar',
      title: 'Aadhaar Card',
      icon: 'ðŸ†”',
      description: 'Unique identification document issued by UIDAI',
      available: !!documents?.aadhaar
    },
    {
      id: 'pan',
      title: 'PAN Card',
      icon: 'ðŸ’³',
      description: 'Permanent Account Number for tax identification',
      available: !!documents?.pan
    },
    {
      id: 'drivingLicense',
      title: 'Driving License',
      icon: 'ðŸš—',
      description: 'Government issued driving permit',
      available: !!documents?.drivingLicense
    },
    {
      id: 'passport',
      title: 'Passport',
      icon: 'ðŸ›‚',
      description: 'International travel document',
      available: !!documents?.passport
    }
  ];

  const handleDocumentClick = (documentId: string) => {
    if (documents) {
      navigate(`/documents/${documentId}`);
    }
  };

  const handleTextView = () => {
    navigate('/documents/text-view');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading documents...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 rounded-xl p-6 text-white">
        <h1 className="text-2xl sm:text-3xl font-bold mb-2">My Documents</h1>
        <p className="text-blue-100">
          View and manage your government documents in one secure place
        </p>
      </div>

      {/* View Options */}
      <div className="card">
        <div className="card-body">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div>
              <h2 className="text-lg font-semibold text-gray-900">Document View Options</h2>
              <p className="text-sm text-gray-600">Choose how you want to view your documents</p>
            </div>
            <button
              onClick={handleTextView}
              className="btn btn-secondary flex items-center gap-2"
            >
              <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
              </svg>
              Text View
            </button>
          </div>
        </div>
      </div>

      {/* Document Cards Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-4 gap-6">
        {documentCards.map((card) => (
          <div
            key={card.id}
            onClick={() => handleDocumentClick(card.id)}
            className={`relative bg-white rounded-xl border-2 p-6 cursor-pointer transition-all duration-200 group hover:shadow-lg ${
              card.available
                ? 'border-green-200 hover:border-green-300 hover:bg-green-50'
                : 'border-gray-200 hover:border-blue-300 hover:bg-blue-50'
            }`}
          >
            {/* Status Badge */}
            <div className="absolute top-4 right-4">
              {card.available ? (
                <div className="bg-green-100 text-green-800 text-xs px-2 py-1 rounded-full font-medium">
                  Available
                </div>
              ) : (
                <div className="bg-gray-100 text-gray-800 text-xs px-2 py-1 rounded-full font-medium">
                  Not Found
                </div>
              )}
            </div>

            {/* Document Icon */}
            <div className="text-4xl mb-4">{card.icon}</div>

            {/* Document Info */}
            <h3 className="text-lg font-semibold text-gray-900 mb-2 group-hover:text-blue-900">
              {card.title}
            </h3>
            <p className="text-sm text-gray-600 mb-4">
              {card.description}
            </p>

            {/* Action Icon */}
            <div className="flex items-center justify-between">
              <div className={`text-sm font-medium ${
                card.available ? 'text-green-600' : 'text-gray-500'
              }`}>
                {card.available ? 'Click to view' : 'No data'}
              </div>
              {card.available && (
                <div className="text-blue-500 opacity-0 group-hover:opacity-100 transition-opacity">
                  <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7"></path>
                  </svg>
                </div>
              )}
            </div>

            {/* Document Preview (for available documents) */}
            {card.available && (
              <div className="mt-4 pt-4 border-t border-gray-100">
                <div className="text-xs text-gray-500">
                  {card.id === 'aadhaar' && documents?.aadhaar && `**** **** ${documents.aadhaar.number.slice(-4)}`}
                  {card.id === 'pan' && documents?.pan && documents.pan.number}
                  {card.id === 'drivingLicense' && documents?.drivingLicense && documents.drivingLicense.number}
                  {card.id === 'passport' && documents?.passport && documents.passport.number}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Stats */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Document Summary</h3>
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-green-600">
              {documentCards.filter(card => card.available).length}
            </div>
            <div className="text-sm text-gray-600">Available</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-600">
              {documentCards.filter(card => !card.available).length}
            </div>
            <div className="text-sm text-gray-600">Missing</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-blue-600">
              {Math.round((documentCards.filter(card => card.available).length / documentCards.length) * 100)}%
            </div>
            <div className="text-sm text-gray-600">Complete</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-indigo-600">4</div>
            <div className="text-sm text-gray-600">Total</div>
          </div>
        </div>
      </div>

      {/* Security Notice */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-yellow-800">
              Security Notice
            </h3>
            <div className="mt-2 text-sm text-yellow-700">
              <p>
                Your documents are securely stored and encrypted. Only you have access to view these documents.
                Never share your document details with unauthorized parties.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Documents;
