import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { schemesAPI } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { Scheme, EligibilityResult } from '../types';
import { toast } from 'react-hot-toast';

const SchemeDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [scheme, setScheme] = useState<Scheme | null>(null);
  const [eligibilityResult, setEligibilityResult] = useState<EligibilityResult | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSchemeDetails = async () => {
      if (!id) {
        navigate('/schemes');
        return;
      }

      try {
        const [schemeRes, eligibilityRes] = await Promise.all([
          schemesAPI.getSchemeById(id),
          schemesAPI.getEligibilityResults(),
        ]);

        if (schemeRes.success && schemeRes.data) {
          setScheme(schemeRes.data);
        } else {
          toast.error('Scheme not found');
          navigate('/schemes');
          return;
        }

        if (eligibilityRes.success && eligibilityRes.data) {
          const userEligibility = eligibilityRes.data.find(
            result => result.scheme.id === id
          );
          if (userEligibility) {
            setEligibilityResult(userEligibility);
          }
        }
      } catch (error: any) {
        console.error('Failed to fetch scheme details:', error);
        toast.error('Failed to load scheme details');
        navigate('/schemes');
      } finally {
        setLoading(false);
      }
    };

    fetchSchemeDetails();
  }, [id, navigate]);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const getEligibilityColor = (score: number) => {
    if (score >= 100) return 'text-green-600';
    if (score >= 75) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getEligibilityBadge = (result: EligibilityResult) => {
    if (result.isEligible) return 'badge-success';
    if (result.eligibilityScore >= 50) return 'badge-warning';
    return 'badge-secondary';
  };

  const getEligibilityText = (result: EligibilityResult) => {
    if (result.isEligible) return 'You are eligible for this scheme!';
    if (result.eligibilityScore >= 50) return 'You are partially eligible for this scheme';
    return 'You are not currently eligible for this scheme';
  };

  const handleApplyClick = () => {
    if (scheme?.officialWebsite) {
      window.open(scheme.officialWebsite, '_blank');
    } else {
      toast('Application process information will be available soon', {
        icon: '‚ÑπÔ∏è',
        style: {
          borderRadius: '10px',
          background: '#3B82F6',
          color: '#fff',
        },
      });
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="spinner mx-auto mb-4"></div>
          <p className="text-gray-600">Loading scheme details...</p>
        </div>
      </div>
    );
  }

  if (!scheme) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500 text-lg mb-4">Scheme not found</p>
        <button
          onClick={() => navigate('/schemes')}
          className="btn btn-primary"
        >
          Back to Schemes
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex items-start justify-between">
        <button
          onClick={() => navigate('/schemes')}
          className="btn btn-secondary mb-4"
        >
          ‚Üê Back to Schemes
        </button>
      </div>

      {/* Scheme Header */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-600 rounded-xl p-8 text-white">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h1 className="text-3xl font-bold mb-2">
              {scheme.name}
            </h1>
            <p className="text-indigo-100 text-lg mb-4">
              {scheme.description}
            </p>
            <div className="flex items-center space-x-4">
              <span className="bg-white bg-opacity-20 px-3 py-1 rounded-full text-sm">
                {scheme.category}
              </span>
              <span className="text-indigo-100 text-sm">
                {scheme.department}
              </span>
            </div>
          </div>
          {eligibilityResult && (
            <div className="ml-8 text-center">
              <div className={`text-4xl font-bold ${
                eligibilityResult.isEligible ? 'text-green-100' : 'text-yellow-100'
              }`}>
                {eligibilityResult.eligibilityScore}%
              </div>
              <div className="text-indigo-100 text-sm">
                Eligibility Score
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Eligibility Status */}
      {eligibilityResult && (
        <div className="card">
          <div className="card-body">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold">Your Eligibility Status</h2>
              <span className={`badge ${getEligibilityBadge(eligibilityResult)} text-lg px-4 py-2`}>
                {eligibilityResult.isEligible ? '‚úì Eligible' : '‚ö† Not Eligible'}
              </span>
            </div>
            
            <p className="text-gray-700 mb-4">
              {getEligibilityText(eligibilityResult)}
            </p>

            {!eligibilityResult.isEligible && eligibilityResult.missingCriteria.length > 0 && (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                <h3 className="font-medium text-yellow-800 mb-2">
                  Requirements you don't meet:
                </h3>
                <ul className="space-y-1">
                  {eligibilityResult.missingCriteria.map((criteria, index) => (
                    <li key={index} className="flex items-start text-yellow-700">
                      <span className="mr-2">‚Ä¢</span>
                      {criteria}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {eligibilityResult.isEligible && (
              <div className="bg-green-50 border border-green-200 rounded-lg p-4 flex items-center justify-between">
                <div>
                  <h3 className="font-medium text-green-800">
                    You meet all requirements for this scheme!
                  </h3>
                  <p className="text-green-700 text-sm">
                    You can proceed with the application process.
                  </p>
                </div>
                <button
                  onClick={handleApplyClick}
                  className="btn btn-success"
                >
                  Apply Now
                </button>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Benefits */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">Benefits</h2>
        </div>
        <div className="card-body">
          <ul className="space-y-3">
            {scheme.benefits.map((benefit, index) => (
              <li key={index} className="flex items-start">
                <span className="text-green-500 mr-3 mt-1">‚úì</span>
                <span className="text-gray-700">{benefit}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Eligibility Criteria */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">Eligibility Criteria</h2>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {scheme.eligibilityCriteria.minAge && (
              <div>
                <h4 className="font-medium text-gray-900 mb-1">Minimum Age</h4>
                <p className="text-gray-600">{scheme.eligibilityCriteria.minAge} years</p>
              </div>
            )}
            {scheme.eligibilityCriteria.maxAge && (
              <div>
                <h4 className="font-medium text-gray-900 mb-1">Maximum Age</h4>
                <p className="text-gray-600">{scheme.eligibilityCriteria.maxAge} years</p>
              </div>
            )}
            {scheme.eligibilityCriteria.maxIncome && (
              <div>
                <h4 className="font-medium text-gray-900 mb-1">Maximum Annual Income</h4>
                <p className="text-gray-600">
                  ‚Çπ{scheme.eligibilityCriteria.maxIncome.toLocaleString()}
                </p>
              </div>
            )}
            {scheme.eligibilityCriteria.gender && scheme.eligibilityCriteria.gender !== 'all' && (
              <div>
                <h4 className="font-medium text-gray-900 mb-1">Gender</h4>
                <p className="text-gray-600 capitalize">{scheme.eligibilityCriteria.gender}</p>
              </div>
            )}
            {scheme.eligibilityCriteria.occupation && scheme.eligibilityCriteria.occupation.length > 0 && (
              <div>
                <h4 className="font-medium text-gray-900 mb-1">Occupation</h4>
                <p className="text-gray-600">
                  {scheme.eligibilityCriteria.occupation.join(', ')}
                </p>
              </div>
            )}
            {scheme.eligibilityCriteria.state && scheme.eligibilityCriteria.state.length > 0 && (
              <div>
                <h4 className="font-medium text-gray-900 mb-1">States</h4>
                <p className="text-gray-600">
                  {scheme.eligibilityCriteria.state.join(', ')}
                </p>
              </div>
            )}
          </div>
          
          {scheme.eligibilityCriteria.customCriteria && scheme.eligibilityCriteria.customCriteria.length > 0 && (
            <div className="mt-6 pt-6 border-t border-gray-200">
              <h4 className="font-medium text-gray-900 mb-3">Additional Requirements</h4>
              <ul className="space-y-2">
                {scheme.eligibilityCriteria.customCriteria.map((requirement: string, index: number) => (
                  <li key={index} className="flex items-start">
                    <span className="text-blue-500 mr-2">‚Ä¢</span>
                    <span className="text-gray-700">{requirement}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>

      {/* Documents Required */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">Documents Required</h2>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {scheme.documentsRequired.map((document, index) => (
              <div key={index} className="flex items-center p-3 bg-gray-50 rounded-lg">
                <span className="text-blue-500 mr-3">üìÑ</span>
                <span className="text-gray-700">{document}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Application Process */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">How to Apply</h2>
        </div>
        <div className="card-body">
          <p className="text-gray-700 mb-4">{scheme.applicationProcess}</p>
          <div className="flex items-center space-x-4">
            <button
              onClick={handleApplyClick}
              className="btn btn-primary"
            >
              Visit Official Website
            </button>
            <a
              href={scheme.officialWebsite}
              target="_blank"
              rel="noopener noreferrer"
              className="text-sm text-blue-600 hover:text-blue-800"
            >
              {scheme.officialWebsite}
            </a>
          </div>
        </div>
      </div>

      {/* Scheme Information */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold">Scheme Information</h2>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-medium text-gray-900 mb-1">Department</h4>
              <p className="text-gray-600">{scheme.department}</p>
            </div>
            <div>
              <h4 className="font-medium text-gray-900 mb-1">Category</h4>
              <p className="text-gray-600">{scheme.category}</p>
            </div>
            <div>
              <h4 className="font-medium text-gray-900 mb-1">Status</h4>
              <p className="text-gray-600">
                {scheme.status === 'active' ? (
                  <span className="text-green-600">Active</span>
                ) : scheme.status === 'inactive' ? (
                  <span className="text-red-600">Inactive</span>
                ) : (
                  <span className="text-yellow-600">Suspended</span>
                )}
              </p>
            </div>
            <div>
              <h4 className="font-medium text-gray-900 mb-1">Last Updated</h4>
              <p className="text-gray-600">{formatDate(scheme.lastUpdated)}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SchemeDetails;
