import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { authAPI } from '../services/api';
import { TestUser } from '../types';
import { toast } from 'react-hot-toast';

const Login: React.FC = () => {
  const { sendOTP, login } = useAuth();
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [step, setStep] = useState<'phone' | 'otp'>('phone');
  const [loading, setLoading] = useState(false);
  const [testUsers, setTestUsers] = useState<TestUser[]>([]);

  useEffect(() => {
    // Load test users for demonstration
    const loadTestUsers = async () => {
      try {
        const response = await authAPI.getTestUsers();
        if (response.success && response.data) {
          setTestUsers(response.data);
        }
      } catch (error) {
        console.error('Failed to load test users:', error);
      }
    };
    loadTestUsers();
  }, []);

  const handleSendOTP = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!phoneNumber.trim()) {
      toast.error('Please enter a phone number');
      return;
    }

    setLoading(true);
    try {
      await sendOTP(phoneNumber);
      setStep('otp');
    } catch (error) {
      // Error is already handled in the auth context
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOTP = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!otp.trim()) {
      toast.error('Please enter the OTP');
      return;
    }

    setLoading(true);
    try {
      await login(phoneNumber, otp);
      // Navigation is handled by the App component
    } catch (error) {
      // Error is already handled in the auth context
    } finally {
      setLoading(false);
    }
  };

  const handleTestUserSelect = (testPhoneNumber: string) => {
    setPhoneNumber(testPhoneNumber);
  };

  const handleBack = () => {
    setStep('phone');
    setOtp('');
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <div className="mx-auto h-12 w-12 text-center">
            <span className="text-4xl">🏛️</span>
          </div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Government Schemes
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Access all government schemes in one place
          </p>
        </div>

        {step === 'phone' && (
          <div className="card">
            <div className="card-body">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                Enter Your Phone Number
              </h3>
              <form onSubmit={handleSendOTP}>
                <div className="form-group">
                  <label htmlFor="phone" className="form-label">
                    Phone Number
                  </label>
                  <input
                    id="phone"
                    type="tel"
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                    className="form-input"
                    placeholder="+919876543210"
                    required
                  />
                </div>
                <button
                  type="submit"
                  disabled={loading}
                  className="btn btn-primary w-full"
                >
                  {loading ? (
                    <span className="flex items-center justify-center">
                      <div className="spinner mr-2"></div>
                      Sending OTP...
                    </span>
                  ) : (
                    'Send OTP'
                  )}
                </button>
              </form>

              {testUsers.length > 0 && (
                <div className="mt-6 pt-6 border-t border-gray-200">
                  <h4 className="text-sm font-medium text-gray-900 mb-3">
                    Demo Users (Click to select)
                  </h4>
                  <div className="space-y-2">
                    {testUsers.map((user, index) => (
                      <button
                        key={index}
                        onClick={() => handleTestUserSelect(user.phoneNumber)}
                        className="w-full text-left p-3 rounded-lg border border-gray-200 hover:border-indigo-300 hover:bg-indigo-50 transition-colors"
                      >
                        <div className="font-medium text-sm text-gray-900">
                          {user.name}
                        </div>
                        <div className="text-xs text-gray-500">
                          {user.phoneNumber}
                        </div>
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {step === 'otp' && (
          <div className="card">
            <div className="card-body">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                Enter OTP
              </h3>
              <p className="text-sm text-gray-600 mb-4">
                We've sent a 6-digit OTP to {phoneNumber}
              </p>
              <form onSubmit={handleVerifyOTP}>
                <div className="form-group">
                  <label htmlFor="otp" className="form-label">
                    OTP
                  </label>
                  <input
                    id="otp"
                    type="text"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    className="form-input text-center text-2xl tracking-widest"
                    placeholder="000000"
                    maxLength={6}
                    required
                  />
                </div>
                <div className="flex space-x-3">
                  <button
                    type="button"
                    onClick={handleBack}
                    className="btn btn-secondary flex-1"
                  >
                    Back
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="btn btn-primary flex-1"
                  >
                    {loading ? (
                      <span className="flex items-center justify-center">
                        <div className="spinner mr-2"></div>
                        Verifying...
                      </span>
                    ) : (
                      'Verify OTP'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        <div className="text-center">
          <p className="text-xs text-gray-500">
            By continuing, you agree to our Terms of Service and Privacy Policy
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
