import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { userAPI } from '../services/api';
import { UserDocuments } from '../types';
import { Link } from 'react-router-dom';
import Loading from '../components/Loading';

const Documents: React.FC = () => {
  const { user } = useAuth();
  const [documents, setDocuments] = useState<UserDocuments | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const response = await userAPI.getUserDocuments();
        if (response.success) {
          setDocuments(response.data || null);
        } else {
          setError(response.message || 'Failed to load documents');
        }
      } catch (err) {
        setError('Network error. Please try again.');
        console.error('Documents error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchDocuments();
  }, []);

  if (loading) return <Loading />;

  const documentTypes = [
    {
      id: 'aadhaar',
      title: 'Aadhaar Card',
      description: 'Unique Identity Document',
      status: documents?.aadhaar ? 'available' : 'missing',
      route: '/documents/aadhaar',
      bgColor: 'bg-orange-50',
      borderColor: 'border-orange-200',
      textColor: 'text-orange-700',
      accentColor: 'bg-orange-500'
    },
    {
      id: 'pan',
      title: 'PAN Card',
      description: 'Permanent Account Number',
      status: documents?.pan ? 'available' : 'missing',
      route: '/documents/pan',
      bgColor: 'bg-blue-50',
      borderColor: 'border-blue-200',
      textColor: 'text-blue-700',
      accentColor: 'bg-blue-500'
    },
    {
      id: 'driving-license',
      title: 'Driving License',
      description: 'Motor Vehicle Authorization',
      status: documents?.drivingLicense ? 'available' : 'missing',
      route: '/documents/driving-license',
      bgColor: 'bg-green-50',
      borderColor: 'border-green-200',
      textColor: 'text-green-700',
      accentColor: 'bg-green-500'
    },
    {
      id: 'passport',
      title: 'Passport',
      description: 'International Travel Document',
      status: documents?.passport ? 'available' : 'missing',
      route: '/documents/passport',
      bgColor: 'bg-indigo-50',
      borderColor: 'border-indigo-200',
      textColor: 'text-indigo-700',
      accentColor: 'bg-indigo-500'
    }
  ];

  const availableDocuments = documentTypes.filter(doc => doc.status === 'available');
  const missingDocuments = documentTypes.filter(doc => doc.status === 'missing');

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="bg-gradient-to-r from-indigo-600 to-purple-600 rounded-2xl p-8 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold mb-2">My Documents</h1>
            <p className="text-indigo-100 text-lg">
              View and manage your government documents in one secure place
            </p>
          </div>
          <div className="hidden md:block">
            <div className="w-20 h-20 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
              <div className="w-10 h-10 bg-white bg-opacity-30 rounded-lg"></div>
            </div>
          </div>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <div className="w-5 h-5 bg-red-500 rounded-full"></div>
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-800">{error}</p>
            </div>
          </div>
        </div>
      )}

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Documents</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">{documentTypes.length}</p>
              <p className="text-sm text-gray-500 mt-1">Available document types</p>
            </div>
            <div className="w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center">
              <div className="w-6 h-6 bg-gray-500 rounded-full"></div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Available</p>
              <p className="text-3xl font-bold text-green-600 mt-2">{availableDocuments.length}</p>
              <p className="text-sm text-gray-500 mt-1">Documents you have</p>
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
              <div className="w-6 h-6 bg-green-500 rounded-full"></div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Missing</p>
              <p className="text-3xl font-bold text-orange-600 mt-2">{missingDocuments.length}</p>
              <p className="text-sm text-gray-500 mt-1">Documents to obtain</p>
            </div>
            <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
              <div className="w-6 h-6 bg-orange-500 rounded-full"></div>
            </div>
          </div>
        </div>
      </div>

      {/* Document View Options */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-6">Document View Options</h2>
        <p className="text-gray-600 mb-6">Choose how you want to view your documents</p>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Link
            to="/documents/text-view"
            className="p-6 border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors duration-200 group"
          >
            <div className="text-center">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:bg-blue-200 transition-colors duration-200">
                <div className="w-8 h-8 bg-blue-500 rounded-lg"></div>
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">Text View</h3>
              <p className="text-sm text-gray-600">View document information in text format for easy copying</p>
            </div>
          </Link>
          
          <div className="p-6 border-2 border-dashed border-gray-300 rounded-lg">
            <div className="text-center">
              <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <div className="w-8 h-8 bg-gray-500 rounded-lg"></div>
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">Document Cards</h3>
              <p className="text-sm text-gray-600">View realistic document clones below</p>
            </div>
          </div>
        </div>
      </div>

      {/* Document Cards */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-6">Your Documents</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {documentTypes.map((doc) => (
            <div key={doc.id} className={`${doc.bgColor} ${doc.borderColor} border-2 rounded-xl p-6 hover:shadow-md transition-shadow duration-200`}>
              <div className="flex items-start justify-between mb-4">
                <div className="flex-1">
                  <div className="flex items-center space-x-3 mb-2">
                    <div className={`w-3 h-3 rounded-full ${doc.status === 'available' ? 'bg-green-500' : 'bg-red-500'}`}></div>
                    <h3 className={`text-lg font-bold ${doc.textColor}`}>{doc.title}</h3>
                  </div>
                  <p className={`text-sm ${doc.textColor} opacity-75 mb-3`}>{doc.description}</p>
                  <div className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium ${
                    doc.status === 'available' 
                      ? 'bg-green-100 text-green-800' 
                      : 'bg-red-100 text-red-800'
                  }`}>
                    {doc.status === 'available' ? 'Available' : 'Not Available'}
                  </div>
                </div>
                <div className={`w-12 h-12 ${doc.accentColor} rounded-lg flex items-center justify-center`}>
                  <div className="w-6 h-6 bg-white rounded-full"></div>
                </div>
              </div>
              
              {doc.status === 'available' && (
                <Link
                  to={doc.route}
                  className={`block w-full text-center py-3 px-4 ${doc.accentColor} text-white rounded-lg font-medium hover:opacity-90 transition-opacity duration-200`}
                >
                  View Document
                </Link>
              )}
              
              {doc.status === 'missing' && (
                <div className="w-full text-center py-3 px-4 bg-gray-300 text-gray-600 rounded-lg font-medium cursor-not-allowed">
                  Document Not Available
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Help Section */}
      <div className="bg-blue-50 rounded-xl p-6 border border-blue-200">
        <h3 className="text-lg font-medium text-blue-900 mb-2">Need Help?</h3>
        <p className="text-sm text-blue-700 mb-4">
          If you're missing any documents, you can apply for them through the respective government portals.
        </p>
        <div className="flex flex-wrap gap-2">
          <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
            Aadhaar: uidai.gov.in
          </span>
          <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
            PAN: incometax.gov.in
          </span>
          <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
            Passport: passportindia.gov.in
          </span>
        </div>
      </div>
    </div>
  );
};

export default Documents;
