import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { schemesAPI } from '../services/api';
import { EligibilityResult } from '../types';
import { toast } from 'react-hot-toast';

const Schemes: React.FC = () => {
  const navigate = useNavigate();
  const [schemes, setSchemes] = useState<EligibilityResult[]>([]);
  const [filteredSchemes, setFilteredSchemes] = useState<EligibilityResult[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [selectedEligibility, setSelectedEligibility] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState('');

  const categories = [
    'all',
    'Agriculture',
    'Education',
    'Housing',
    'Business',
    'Health',
    'Women Empowerment',
    'Skill Development',
    'Financial Services',
    'Social Security'
  ];

  const eligibilityFilters = [
    { value: 'all', label: 'All Schemes' },
    { value: 'eligible', label: 'Eligible Only' },
    { value: 'partially', label: 'Partially Eligible' },
    { value: 'not-eligible', label: 'Not Eligible' }
  ];

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await schemesAPI.getEligibilityResults();
        if (response.success && response.data) {
          setSchemes(response.data);
          setFilteredSchemes(response.data);
        }
      } catch (error: any) {
        console.error('Failed to fetch schemes:', error);
        toast.error('Failed to load schemes');
      } finally {
        setLoading(false);
      }
    };

    fetchSchemes();
  }, []);

  useEffect(() => {
    let filtered = [...schemes];

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(
        result => result.scheme.category === selectedCategory
      );
    }

    // Filter by eligibility
    if (selectedEligibility === 'eligible') {
      filtered = filtered.filter(result => result.isEligible);
    } else if (selectedEligibility === 'partially') {
      filtered = filtered.filter(
        result => !result.isEligible && result.eligibilityScore >= 50
      );
    } else if (selectedEligibility === 'not-eligible') {
      filtered = filtered.filter(
        result => !result.isEligible && result.eligibilityScore < 50
      );
    }

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(result =>
        result.scheme.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        result.scheme.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        result.scheme.department.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredSchemes(filtered);
  }, [schemes, selectedCategory, selectedEligibility, searchTerm]);

  const handleSchemeClick = (schemeId: string) => {
    navigate(`/schemes/${schemeId}`);
  };

  const getEligibilityColor = (score: number) => {
    if (score >= 100) return 'text-green-600';
    if (score >= 75) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getEligibilityBadge = (result: EligibilityResult) => {
    if (result.isEligible) return 'badge-success';
    if (result.eligibilityScore >= 50) return 'badge-warning';
    return 'badge-secondary';
  };

  const getEligibilityText = (result: EligibilityResult) => {
    if (result.isEligible) return 'Eligible';
    if (result.eligibilityScore >= 50) return 'Partially Eligible';
    return 'Not Eligible';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="spinner mx-auto mb-4"></div>
          <p className="text-gray-600">Loading schemes...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-600 rounded-xl p-8 text-white">
        <h1 className="text-3xl font-bold mb-2">
          Government Schemes
        </h1>
        <p className="text-indigo-100">
          Explore all available government schemes and check your eligibility
        </p>
      </div>

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Search */}
            <div>
              <label className="form-label">Search Schemes</label>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="form-input"
                placeholder="Search by name, description, or department..."
              />
            </div>

            {/* Category Filter */}
            <div>
              <label className="form-label">Category</label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="form-input"
              >
                {categories.map(category => (
                  <option key={category} value={category}>
                    {category === 'all' ? 'All Categories' : category}
                  </option>
                ))}
              </select>
            </div>

            {/* Eligibility Filter */}
            <div>
              <label className="form-label">Eligibility</label>
              <select
                value={selectedEligibility}
                onChange={(e) => setSelectedEligibility(e.target.value)}
                className="form-input"
              >
                {eligibilityFilters.map(filter => (
                  <option key={filter.value} value={filter.value}>
                    {filter.label}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* Results Summary */}
      <div className="flex items-center justify-between">
        <p className="text-gray-600">
          Showing {filteredSchemes.length} of {schemes.length} schemes
        </p>
        <div className="flex items-center space-x-2">
          <span className="text-sm text-gray-500">Sort by:</span>
          <select className="text-sm border-gray-300 rounded">
            <option>Eligibility Score</option>
            <option>Name</option>
            <option>Category</option>
          </select>
        </div>
      </div>

      {/* Schemes List */}
      <div className="space-y-4">
        {filteredSchemes.length === 0 ? (
          <div className="card">
            <div className="card-body text-center py-12">
              <p className="text-gray-500 text-lg mb-4">
                No schemes found matching your criteria
              </p>
              <button
                onClick={() => {
                  setSelectedCategory('all');
                  setSelectedEligibility('all');
                  setSearchTerm('');
                }}
                className="btn btn-secondary"
              >
                Clear Filters
              </button>
            </div>
          </div>
        ) : (
          filteredSchemes.map((result) => (
            <div
              key={result.scheme.id}
              className="card hover:shadow-md cursor-pointer transition-shadow"
              onClick={() => handleSchemeClick(result.scheme.id)}
            >
              <div className="card-body">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3 mb-2">
                      <h3 className="text-lg font-semibold text-gray-900">
                        {result.scheme.name}
                      </h3>
                      <span className={`badge ${getEligibilityBadge(result)}`}>
                        {getEligibilityText(result)}
                      </span>
                    </div>
                    
                    <p className="text-gray-600 mb-3">
                      {result.scheme.description}
                    </p>
                    
                    <div className="flex items-center space-x-4 mb-3">
                      <span className="badge badge-primary">
                        {result.scheme.category}
                      </span>
                      <span className="text-sm text-gray-500">
                        {result.scheme.department}
                      </span>
                    </div>

                    {/* Benefits Preview */}
                    <div className="mb-3">
                      <h4 className="text-sm font-medium text-gray-900 mb-1">
                        Key Benefits:
                      </h4>
                      <ul className="text-sm text-gray-600">
                        {result.scheme.benefits.slice(0, 2).map((benefit, index) => (
                          <li key={index} className="flex items-start">
                            <span className="text-green-500 mr-2">•</span>
                            {benefit}
                          </li>
                        ))}
                        {result.scheme.benefits.length > 2 && (
                          <li className="text-gray-500 italic">
                            +{result.scheme.benefits.length - 2} more benefits
                          </li>
                        )}
                      </ul>
                    </div>

                    {/* Eligibility Info */}
                    {!result.isEligible && result.missingCriteria.length > 0 && (
                      <div className="bg-yellow-50 border border-yellow-200 rounded p-3">
                        <h5 className="text-sm font-medium text-yellow-800 mb-1">
                          Missing Requirements:
                        </h5>
                        <ul className="text-sm text-yellow-700">
                          {result.missingCriteria.slice(0, 2).map((criteria, index) => (
                            <li key={index} className="flex items-start">
                              <span className="mr-2">•</span>
                              {criteria}
                            </li>
                          ))}
                          {result.missingCriteria.length > 2 && (
                            <li className="italic">
                              +{result.missingCriteria.length - 2} more requirements
                            </li>
                          )}
                        </ul>
                      </div>
                    )}
                  </div>
                  
                  <div className="ml-6 text-right">
                    <div className={`text-2xl font-bold ${getEligibilityColor(result.eligibilityScore)}`}>
                      {result.eligibilityScore}%
                    </div>
                    <div className="text-sm text-gray-500">
                      Eligibility Score
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Schemes;
