import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { authAPI } from '../services/api';
import { TestUser } from '../types';
import { toast } from 'react-hot-toast';

const Login: React.FC = () => {
  const { login } = useAuth();
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [step, setStep] = useState<'phone' | 'otp'>('phone');
  const [loading, setLoading] = useState(false);
  const [testUsers, setTestUsers] = useState<TestUser[]>([]);
  const [connectionError, setConnectionError] = useState<string | null>(null);
  const [demoOtp, setDemoOtp] = useState<string | null>(null);
  const [loginSuccess, setLoginSuccess] = useState(false);

  useEffect(() => {
    // Load test users for demonstration
    const loadTestUsers = async () => {
      try {
        setConnectionError(null);
        const response = await authAPI.getTestUsers();
        if (response.success && response.data) {
          setTestUsers(response.data);
        }
      } catch (error: any) {
        console.error('Failed to load test users:', error);
        setConnectionError('Failed to connect to server. Please ensure the backend is running.');
        toast.error('Connection Error: Backend server may not be running');
      }
    };
    loadTestUsers();
  }, []);

  const handleSendOTP = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!phoneNumber.trim()) {
      toast.error('Please enter a phone number');
      return;
    }

    // Basic phone number validation
    if (phoneNumber.length < 10) {
      toast.error('Please enter a valid phone number (minimum 10 digits)');
      return;
    }

    setLoading(true);
    setConnectionError(null);
    try {
      const response = await authAPI.sendOTP(phoneNumber);
      if (response.success && response.data?.otp) {
        // Auto-fill the OTP for demo purposes
        setDemoOtp(response.data.otp);
        setOtp(response.data.otp);
        setStep('otp');
        toast.success('OTP sent successfully! Demo OTP has been auto-filled.', { duration: 5000 });
      } else {
        setStep('otp');
        toast.success('OTP sent successfully!');
      }
    } catch (error: any) {
      console.error('Send OTP error:', error);
      if (error.code === 'ECONNREFUSED' || error.code === 'ERR_NETWORK') {
        setConnectionError('Cannot connect to server. Please ensure the backend is running on port 5000.');
        toast.error('Connection Error: Backend server is not reachable');
      } else {
        const errorMessage = error.response?.data?.message || error.message || 'Failed to send OTP';
        toast.error(errorMessage);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOTP = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!otp.trim()) {
      toast.error('Please enter the OTP');
      return;
    }

    if (otp.length !== 6) {
      toast.error('OTP must be 6 digits');
      return;
    }

    setLoading(true);
    try {
      await login(phoneNumber, otp);
      setLoginSuccess(true);
      // Navigation is handled by React Router automatically
    } catch (error: any) {
      console.error('Verify OTP error:', error);
      const errorMessage = error.response?.data?.message || error.message || 'Failed to verify OTP';
      toast.error(errorMessage);
      setLoginSuccess(false);
    } finally {
      setLoading(false);
    }
  };

  const handleTestUserSelect = (testPhoneNumber: string) => {
    setPhoneNumber(testPhoneNumber);
    setConnectionError(null);
  };

  const handleBack = () => {
    setStep('phone');
    setOtp('');
    setDemoOtp(null);
    setLoginSuccess(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 flex items-center justify-center py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-6">
        {/* Header */}
        <div className="text-center">
          <div className="mx-auto h-16 w-16 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-full flex items-center justify-center shadow-xl">
            <span className="text-2xl">üèõÔ∏è</span>
          </div>
          <h1 className="mt-6 text-3xl font-bold text-gray-900">
            Government Schemes Portal
          </h1>
          <p className="mt-2 text-sm text-gray-600">
            Access all government schemes in one secure place
          </p>
        </div>

        {/* Connection Error Alert */}
        {connectionError && (
          <div className="bg-red-50 border border-red-200 rounded-xl p-4">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <h3 className="text-sm font-medium text-red-800">
                  Connection Error
                </h3>
                <div className="mt-2 text-sm text-red-700">
                  <p>{connectionError}</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Phone Number Step */}
        {step === 'phone' && (
          <div className="bg-white rounded-2xl shadow-xl p-8 border border-gray-100">
            <div className="space-y-6">
              <div className="text-center">
                <h2 className="text-xl font-semibold text-gray-900 mb-2">
                  Enter Your Phone Number
                </h2>
                <p className="text-sm text-gray-600">
                  We'll send you a secure OTP to verify your identity
                </p>
              </div>
              
              <form onSubmit={handleSendOTP} className="space-y-6">
                <div>
                  <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                    Phone Number
                  </label>
                  <input
                    id="phone"
                    type="tel"
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 text-base"
                    placeholder="+919876543210"
                    required
                  />
                </div>
                
                <button
                  type="submit"
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-3 px-4 rounded-xl font-medium hover:from-blue-700 hover:to-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 shadow-lg hover:shadow-xl"
                >
                  {loading ? (
                    <span className="flex items-center justify-center">
                      <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Sending OTP...
                    </span>
                  ) : (
                    'Send OTP'
                  )}
                </button>
              </form>

              {/* Test Users Section */}
              {testUsers.length > 0 && (
                <div className="pt-6 border-t border-gray-200">
                  <h3 className="text-sm font-medium text-gray-900 mb-4 flex items-center">
                    <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded-full mr-2">DEMO</span>
                    Test Users (Click to select)
                  </h3>
                  <div className="space-y-3 max-h-64 overflow-y-auto">
                    {testUsers.map((user, index) => (
                      <button
                        key={index}
                        onClick={() => handleTestUserSelect(user.phoneNumber)}
                        className="w-full text-left p-4 rounded-xl border border-gray-200 hover:border-blue-300 hover:bg-blue-50 transition-all duration-200 group"
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex-1 min-w-0">
                            <div className="font-medium text-sm text-gray-900 group-hover:text-blue-900 truncate">
                              {user.name}
                            </div>
                            <div className="text-xs text-gray-500 mt-1">
                              {user.phoneNumber}
                            </div>
                          </div>
                          <div className="text-blue-500 opacity-0 group-hover:opacity-100 transition-opacity ml-2">
                            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7"></path>
                            </svg>
                          </div>
                        </div>
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* OTP Verification Step */}
        {step === 'otp' && (
          <div className="bg-white rounded-2xl shadow-xl p-8 border border-gray-100">
            <div className="space-y-6">
              <div className="text-center">
                <div className="mx-auto h-16 w-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
                  <svg className="h-8 w-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"></path>
                  </svg>
                </div>
                <h2 className="text-xl font-semibold text-gray-900 mb-2">
                  Enter Verification Code
                </h2>
                <p className="text-sm text-gray-600">
                  We've sent a 6-digit OTP to <span className="font-medium text-gray-900">{phoneNumber}</span>
                </p>
                {demoOtp && (
                  <div className="mt-4 p-3 bg-blue-50 rounded-xl">
                    <p className="text-sm text-blue-700">
                      <span className="font-medium">Demo OTP:</span> {demoOtp} (auto-filled)
                    </p>
                  </div>
                )}
              </div>
              
              <form onSubmit={handleVerifyOTP} className="space-y-6">
                <div>
                  <label htmlFor="otp" className="block text-sm font-medium text-gray-700 mb-2">
                    Verification Code
                  </label>
                  <input
                    id="otp"
                    type="text"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    className="w-full px-4 py-4 border border-gray-300 rounded-xl text-center text-xl tracking-widest font-mono focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200"
                    placeholder="000000"
                    maxLength={6}
                    required
                  />
                </div>
                
                <div className="flex flex-col space-y-3">
                  <button
                    type="submit"
                    disabled={loading || loginSuccess}
                    className={`w-full py-3 px-4 rounded-xl font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 shadow-lg hover:shadow-xl ${
                      loginSuccess 
                        ? 'bg-green-600 hover:bg-green-700 focus:ring-green-500' 
                        : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 focus:ring-blue-500'
                    } text-white`}
                  >
                    {loading ? (
                      <span className="flex items-center justify-center">
                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                        Verifying...
                      </span>
                    ) : loginSuccess ? (
                      <span className="flex items-center justify-center">
                        <svg className="h-5 w-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
                        </svg>
                        Login Successful!
                      </span>
                    ) : (
                      'Verify & Login'
                    )}
                  </button>
                  
                  <button
                    type="button"
                    onClick={handleBack}
                    className="w-full bg-gray-100 text-gray-700 py-3 px-4 rounded-xl font-medium hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500 transition-all duration-200"
                  >
                    Back to Phone Number
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Footer */}
        <div className="text-center">
          <p className="text-xs text-gray-500">
            Secure authentication powered by Government of India
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
